mapper.update:
update userpurchasedproduct 
set usedtime = usedtime + #{minute} , availabletime = availabletime - #{minute}
where id = #{id} and uppcode = #{uppcode}

update userpurchasedproduct 
set usedday = usedday + #{hour} , availableday = availableday - #{hour}
where id = #{id} and uppcode = #{uppcode}
